import React from 'react';
import ReactDOM from 'react-dom';
import Header from './components/Header';
import Tasks from './components/Tasks';
import { useState } from 'react'
import './index.css';

class Square extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            value: null,
        };
    }

    render() {
      return (
        <button     className="square" 
                    onClick={ () => this.setState({value: 'X'})}>
          {this.state.value}
        </button>
      );
    }
}

class Board extends React.Component {
constructor(props) {
    super(props);
    this.state = {
        squares: Array(9).fill(null),
    };
}

renderSquare(i) {
    return <Square value={this.state.squares[i]} />;
}

render() {
    const status = 'Next player: X';

    return (
    <div>
        <div className="status">{status}</div>
        <div className="board-row">
        {this.renderSquare(0)}
        {this.renderSquare(1)}
        {this.renderSquare(2)}
        </div>
        <div className="board-row">
        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}
        </div>
        <div className="board-row">
        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
        </div>
    </div>
    );
}
}

class Game extends React.Component {
render() {
    return (
    <div className="App">
        <h1>Hello Bart Im doing React</h1>
        <div className="game-board">
        <Board />
        </div>
        <div className="game-info">
        <div>{/* status */}</div>
        <ol>{/* TODO */}</ol>
        </div>
    </div>

    );
}
}


const Index = () => {
    const [tasks, setTasks] = useState(
        [
            {
                id: 1,
                text: '20 push ups',
                day: '29 juli at 14:30',
                reminder: true,
            },
            {
                id: 2,
                text: '3 uur react tutorial volgen',
                day: '29 july at 15:00',
                reminder: true,
            },
            {
                id: 3,
                text: '3 uur portfolio materiaal verzamelen',
                day: '29 july at 16:30',
                reminder: true,
            },
            {
                id: 4,
                text: 'Body lotion halen, deo, tandpasta, douchegel en shampoo',
                day: '29 july at 17:30',
                reminder: false,
            },
        ]
    ) 

    return (
        <div className="container">
            <Header title={'This is the title of the thing'} />
            <Tasks />
            <Game />
        </div>
    )

}

// ========================================

ReactDOM.render(
<Index />,
document.getElementById('root')
);
